import React,{ useRef ,useEffect,useState}  from 'react';
import { motion, useTransform, useScroll } from "framer-motion";
import Navbar from './Navbar';
import '../styles/Home.css'; // Import the CSS file
import Wave from '../wave.png'
import { useNavigate } from 'react-router-dom';

const Item = props => {
  return <div 
    className="slider-item" 
    style={{
      ":not(:last-of-type)": {
        marginRight: 12,
      }
    }}
    onClick={props.onClick}>
      {props.children}
    </div>
}

const App = () => {
  const navigate = useNavigate();
  const containerRef = React.useRef(null)
  const styles = {
    hidescroll: {
      width: '100%',
      overflow: 'hidden',
    },
    container: {
      width: '100%',
      overflowX: 'auto',
      paddingBottom: 20,
      marginBottom: -20,
    },
  }

  const goTo = (n) => {
    containerRef.current.scrollLeft = containerRef.current.children[0].children[n-1].offsetLeft
  }

  const goToByElem = (elem) => {
    containerRef.current.scrollLeft = elem.offsetLeft;
  }

  const handleClick = (e) => {
    goToByElem(e.target)
  }





  return (


    <div>

      <Navbar />

      {/* Section 1 */}
      <div className="section section1" style={{ backgroundImage: `url(${Wave})` }}>
        <div className="left-part">
            <h2>I'm Aspiring Data Analyst With Strong Analytical Skills, Committed to Transforming Data into Actionable Insights.</h2>

            </div>

            {/* Right Part */}
            <div className="right-part">

            </div>
      </div>

      {/* Section 2 */}

      <div className="section section2">
        <h1>
          I Excel in Crafting Powerful Analytics Projects, Business Intelligence Dashboards, and Insightful Data  Reports.
        </h1>



          <button onClick={
            ()=>navigate('/projects')
          }>
            Explore Projects
          </button>

      </div>

      {/* Section 3 */}

      <div className="slider-wrapper" style={styles.hidescroll}>
      <div className="slider-container" style={styles.container} ref={containerRef}>
        <ul className="slider-list">
          <Item onClick={handleClick}>1</Item>
          <Item onClick={handleClick}>2</Item>
          <Item onClick={handleClick}>3</Item>
          <Item onClick={handleClick}>4</Item>
          <Item onClick={handleClick}>5</Item>
          <Item onClick={handleClick}>6</Item>
          <Item onClick={handleClick}>7</Item>
          <Item onClick={handleClick}>8</Item>
        </ul>
      </div>
    </div>


      {/* Section 4 */}
      <div className="section section4">
        {/* Content for Section 4 */}
      </div>

      {/* Section 5 */}
      <div className="section section5">
        {/* Content for Section 5 */}
      </div>
    </div>


  );
};



export default App;
