// src/components/Navbar.js

import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();

  const handleHomeClick = () => {
    // Handle the home link click behavior.
    navigate('/');
  };

  const handleProjectsClick = () => {
    // Handle the projects button click behavior.
    navigate('/projects');
  };

  const handleCertificationsClick = () => {
    // Handle the certifications button click behavior.
    navigate('/certifications');
  };

  return (
    <div className="navbar" style={navbarStyle}>
      {/* Left Side (Name) */}
      <div className="name-link" style={nameLinkStyle} onClick={handleHomeClick}>
        Dhruv
      </div>

      {/* Right Side (On-page Navigations) */}
      <div className="nav-links" style={navLinksStyle}>
        <button style={buttonStyle} onClick={handleProjectsClick}>
          Projects
        </button>
        <button style={buttonStyle} onClick={handleCertificationsClick}>
          Certifications
        </button>
      </div>
    </div>
  );
};

// Styles
const navbarStyle = {
  height: '10vh',
  width: '100vw',
  backgroundColor: 'black',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  // padding: '0 20px', // Add padding as needed
};

const nameLinkStyle = {
  color: 'white',
  cursor: 'pointer',
  padding: '10px', // Add padding as needed
  margin:'2vw'
};

const navLinksStyle = {
  display: 'flex',
};

const buttonStyle = {
  backgroundColor: 'black',
  color: 'white',
  border: 'none',
  cursor: 'pointer',
  padding: '10px 15px', // Add padding as needed
  margin: '2vh', // Add margin as needed
};

export default Navbar;
