import React,{ useRef }  from 'react';
import { motion, useTransform, useScroll } from "framer-motion";
import Navbar from './Navbar';
import '../styles/Home.css'; // Import the CSS file
import Wave from '../wave.png'
const App = () => {


  return (


    <div>

      <Navbar />

      {/* Section 1 */}
      <div className="section section1" style={{ backgroundImage: `url(${Wave})` }}>
        <div className="left-part">
            <h2>I'm Aspiring Data Analyst With Strong Analytical Skills, Committed to Transforming Data into Actionable Insights.</h2>

            </div>

            {/* Right Part */}
            <div className="right-part">

            </div>
      </div>

      {/* Section 2 */}

      <div className="section section2">
        <h1>
          I Excel in Crafting Powerful Analytics Projects, Business Intelligence Dashboards, and Insightful Data  Reports.
        </h1>
        <h1 className="text-3xl font-bold underline">
      Hello world!
    </h1>


          <button>
            Explore Projects
          </button>

      </div>

      {/* Section 3 */}

      <div className="bg-neutral-800">

      <HorizontalScrollCarousel />

      </div>

      {/* Section 4 */}
      <div className="section section4">
        {/* Content for Section 4 */}
      </div>

      {/* Section 5 */}
      <div className="section section5">
        {/* Content for Section 5 */}
      </div>
    </div>


  );
};

const HorizontalScrollCarousel = () => {
  const targetRef = useRef(null);
  const { scrollYProgress } = useScroll({
    target: targetRef,
  });

  const x = useTransform(scrollYProgress, [0, 1], ["1%", "-95%"]);

  return (
    <section ref={targetRef} className="relative h-[300vh] bg-neutral-900">
      <div className="sticky top-0 flex h-screen items-center overflow-hidden">
        <motion.div style={{ x }} className="flex gap-4">
          {cards.map((card) => {
            return <Card card={card} key={card.id} />;
          })}
        </motion.div>
      </div>
    </section>
  );
};

const Card = ({ card }) => {
  return (
    <div
      key={card.id}
      className="group relative h-[450px] w-[450px] overflow-hidden bg-neutral-200"
    >
      <div
        style={{
          backgroundImage: `url(${card.url})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
        className="absolute inset-0 z-0 transition-transform duration-300 group-hover:scale-110"
      ></div>
      <div className="absolute inset-0 z-10 grid place-content-center">
        <p className="bg-gradient-to-br from-white/20 to-white/0 p-8 text-6xl font-black uppercase text-white backdrop-blur-lg">
          {card.title}
        </p>
      </div>
    </div>
  );
};


export default App;

const cards = [
  {
    url: "/imgs/abstract/1.jpg",
    title: "Title 1",
    id: 1,
  },
  {
    url: "/imgs/abstract/2.jpg",
    title: "Title 2",
    id: 2,
  },
  {
    url: "/imgs/abstract/3.jpg",
    title: "Title 3",
    id: 3,
  },
  {
    url: "/imgs/abstract/4.jpg",
    title: "Title 4",
    id: 4,
  },
  {
    url: "/imgs/abstract/5.jpg",
    title: "Title 5",
    id: 5,
  },
  {
    url: "/imgs/abstract/6.jpg",
    title: "Title 6",
    id: 6,
  },
  {
    url: "/imgs/abstract/7.jpg",
    title: "Title 7",
    id: 7,
  },
];
