import React from 'react';
import Navbar from './Navbar';
import '../styles/Home.css'; // Import the CSS file

const App = () => {
  document.addEventListener('mousemove', (e) => {
    const cursor = document.querySelector('.custom-cursor');
    const currentSection = getCurrentSection(e.clientX, e.clientY);

    // Check the background color of the current section
    if (currentSection === 'white') {
      cursor.style.backgroundColor = 'black';
    } else {
      cursor.style.backgroundColor = 'white';
    }

    // Update cursor position
    cursor.style.left = e.clientX + 'px';
    cursor.style.top = e.clientY + 'px';
  });

  function getCurrentSection(x, y) {
    // Replace this logic with your actual logic to determine the current section or background color
    // For example, you might use x and y coordinates to check against section boundaries
    // This is a simplified example
    if (y < window.innerHeight / 2) {
      return 'black'; // Assume the top half of the screen is black
    } else {
      return 'white'; // Assume the bottom half of the screen is white
    }
  }
  document.addEventListener('mousemove', (e) => {
    const cursor = document.querySelector('.custom-cursor');
    cursor.style.left = e.pageX + 'px';
    cursor.style.top = e.pageY + 'px';
  });

  return (


    <div>
       <div className="custom-cursor">
       <div className="inner-dot"></div>
       </div>
      <Navbar />

      {/* Section 1 */}
      <div className="section section1">
        <div className="left-part">
            <h2>Dhruv is Aspiring Data Analyst With Strong Analytical Skills, Committed to Transforming Data into Actionable Insights.</h2>

            </div>

            {/* Right Part */}
            <div className="right-part">
            {/* Content for the right part, if needed */}
            </div>
      </div>

      {/* Section 2 */}

      <div className="section section2">
        {/* Content for Section 2 */}
      </div>

      {/* Section 3 */}
      <div className="section section3">
        {/* Content for Section 3 */}
      </div>

      {/* Section 4 */}
      <div className="section section4">
        {/* Content for Section 4 */}
      </div>

      {/* Section 5 */}
      <div className="section section5">
        {/* Content for Section 5 */}
      </div>
    </div>


  );
};

export default App;
