// src/components/Navbar.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const [navbarOpacity, setNavbarOpacity] = useState(0);

  const handleHomeClick = () => {
    navigate('/');
  };

  const handleProjectsClick = () => {
    navigate('/projects');
  };

  const handleCertificationsClick = () => {
    navigate('/certifications');
  };

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const maxScroll = window.innerHeight * 0.2;

      // Use position: fixed until scrollPosition reaches 20% of the screen
      const isFixed = scrollPosition < maxScroll;

      // Gradually reduce opacity after 20% scroll
      const opacity = isFixed ? 0 : 1- Math.min((scrollPosition - maxScroll) / maxScroll, 1);

      setNavbarOpacity(opacity);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className={`navbar ${navbarOpacity > 0 ? 'fixed' : ''}`} style={{ opacity: navbarOpacity }}>
      <div className="name-link" onClick={handleHomeClick}>
        Dhruv
      </div>
      <div className="nav-links">
        <button className="button-style" onClick={handleProjectsClick}>
          Projects
        </button>
        <button className="button-style" onClick={handleCertificationsClick}>
          Certifications
        </button>
      </div>
    </div>
  );
};

export default Navbar;
