import React, { Component } from 'react';
import { render } from 'react-dom';
import '../styles/SliderStyles.css';

const Item = props => {
  return <div 
    className="slider-item" 
    style={{
      ":not(:last-of-type)": {
        marginRight: 12,
      }
    }}
    onClick={props.onClick}>
      {props.children}
    </div>
}


const Slider = props => {
  const containerRef = React.useRef(null)
  const styles = {
    hidescroll: {
      width: '100%',
      overflow: 'hidden',
    },
    container: {
      width: '100%',
      overflowX: 'auto',
      paddingBottom: 20,
      marginBottom: -20,
    },
  }

  const goTo = (n) => {
    containerRef.current.scrollLeft = containerRef.current.children[0].children[n-1].offsetLeft
  }

  const goToByElem = (elem) => {
    containerRef.current.scrollLeft = elem.offsetLeft;
  }

  const handleClick = (e) => {
    goToByElem(e.target)
  }

  return (
    <div className="slider-wrapper" style={styles.hidescroll}>
      <div className="slider-container" style={styles.container} ref={containerRef}>
        <ul className="slider-list">
          <Item onClick={handleClick}>1</Item>
          <Item onClick={handleClick}>2</Item>
          <Item onClick={handleClick}>3</Item>
          <Item onClick={handleClick}>4</Item>

        </ul>
      </div>
    </div>
  )

}

export default Slider
