import React, { useRef, useState, useEffect } from 'react';
import '../styles/SliderStyles.css';

const Item = ({ onClick, index, isCentered }) => {
  const itemRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          // If the element is in the viewport, add a class to increase size
          itemRef.current.classList.add('active');
        } else {
          // If the element is not in the viewport, remove the class
          itemRef.current.classList.remove('active');
        }
      },
      { threshold: 0.5 } // Adjust the threshold as needed
    );

    observer.observe(itemRef.current);

    return () => {
      observer.disconnect();
    };
  }, []);

  return (
    <div
      ref={itemRef}
      className={`slider-item ${isCentered ? 'active' : ''}`}
      onClick={() => onClick(index)}
    >
      {index + 1}
    </div>
  );
};

const Slider = () => {
  const containerRef = useRef(null);

  const goTo = (n) => {
    containerRef.current.scrollLeft = containerRef.current.children[0].children[n - 1].offsetLeft;
  };

  const handleClick = (n) => {
    goTo(n);
  };

  return (
    <div className="slider-wrapper">
      <div className="slider-container" ref={containerRef}>
        <div className="slider-list">
          {[1, 2, 3, 4].map((index) => (
            <Item key={index} onClick={handleClick} index={index} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Slider;
