import React,{ useRef ,useEffect,useState}  from 'react';
import { motion, useTransform, useScroll } from "framer-motion";
import Navbar from './Navbar';
import '../styles/Home.css'; // Import the CSS file
import Wave from '../wave.png'
import { useNavigate } from 'react-router-dom';
const App = () => {
  const navigate = useNavigate();


  //horizontal scroll
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleScroll = (e) => {
    setScrollPosition(e.target.scrollLeft);
  };

  useEffect(() => {
    const scrollableList = document.getElementById('scrollable-list');
    const items = document.querySelectorAll('.list-item');
    const center = Math.floor(items.length / 2);
    let closest = null;
    let distance = Infinity;

    items.forEach((item, index) => {
      const rect = item.getBoundingClientRect();
      const centerOffset = Math.abs(scrollableList.offsetWidth / 2 - (rect.left + rect.width / 2));

      if (centerOffset < distance) {
        distance = centerOffset;
        closest = index;
      }
    });

    items.forEach((item, index) => {
      item.classList.toggle('active', index === closest);
    });
  }, [scrollPosition]);

  return (


    <div>

      <Navbar />

      {/* Section 1 */}
      <div className="section section1" style={{ backgroundImage: `url(${Wave})` }}>
        <div className="left-part">
            <h2>I'm Aspiring Data Analyst With Strong Analytical Skills, Committed to Transforming Data into Actionable Insights.</h2>

            </div>

            {/* Right Part */}
            <div className="right-part">

            </div>
      </div>

      {/* Section 2 */}

      <div className="section section2">
        <h1>
          I Excel in Crafting Powerful Analytics Projects, Business Intelligence Dashboards, and Insightful Data  Reports.
        </h1>



          <button onClick={
            ()=>navigate('/projects')
          }>
            Explore Projects
          </button>

      </div>

      {/* Section 3 */}
      <div className="section section3">
          <div id="scrollable-list" className="scrollable-list" onScroll={handleScroll}>
          {/* Add more list items as needed */}
            <div className="list-item">
            <img src="wave.jpg" alt="cdd"/>
              <p>Title 1</p>
            </div>
            <div className="list-item">
              <img src="../wave.jpg" alt="cdd"/>
              <p>Title 2</p>
            </div>
          {/* Add more list items as needed */}
        </div>
      </div>


      {/* Section 4 */}
      <div className="section section4">
        {/* Content for Section 4 */}
      </div>

      {/* Section 5 */}
      <div className="section section5">
        {/* Content for Section 5 */}
      </div>
    </div>


  );
};



export default App;
