import React, { Component } from 'react';
import { render } from 'react-dom';
import '../styles/SliderStyles.css';
import BIDashbord from '../assets/dashbord.jpg';
import MobilDashbord from '../assets/mobildashbord.png';
import AnalyticsReport from '../assets/analyticalReport.jpg';
import MobilDevelopment from '../assets/mobildevelopment.jpg';

const Item = props => {
  return <div 
    className="slider-item" 
    style={{
      ":not(:last-of-type)": {
        marginRight: 12,
      }
    }}
    onClick={()=>{
        alert(props.children)
    }}>
      {props.children}
    </div>
}


const Slider = props => {
  const containerRef = React.useRef(null)
  const styles = {
    hidescroll: {
      width: '100%',
      overflow: 'hidden',
    },
    container: {
      width: '100%',
      overflowX: 'auto',
      paddingBottom: 20,
      marginBottom: -20,
    },
  }

  const goTo = (n) => {
    containerRef.current.scrollLeft = containerRef.current.children[0].children[n-1].offsetLeft
  }

  const goToByElem = (elem) => {
    containerRef.current.scrollLeft = elem.offsetLeft;
  }

  const handleClick = (e) => {
    goToByElem(e.target)
  }

  return (
    <div className="slider-wrapper" style={styles.hidescroll}>
      <div className="slider-container" style={styles.container} ref={containerRef}>
        <ul className="slider-list">
          <Item onClick={handleClick}>
            <img src={BIDashbord} alt={"Interactive Business Intelligence Dashboards"}/>
          </Item>
          <Item onClick={handleClick}>
            <img src={MobilDashbord} alt={"Interactive Mobil Dashboards"}/>
          </Item>
          <Item onClick={handleClick}>
          <img src={AnalyticsReport} alt={"Interactive Business Intelligence Dashboards"}/>
          </Item>
          <Item onClick={handleClick}>4</Item>

        </ul>
      </div>
    </div>
  )

}

export default Slider
