// SplitSection.js
import React from 'react';
import '../styles/SplitSection.css'; // Import your styles
import Google from '../assets/google.png';
import Microsoft from '../assets/microsoft.png';

const SplitSection = () => {
  return (
    <div className="split-section">
      <LeftHalf />
      <RightHalf />
    </div>
  );
};

const LeftHalf = () => (
  <div className="left-half">
    <img className="image" src={Google} alt="Left Image" />
    <div>
      <h2>Google Analytics </h2>
      <p>Professional Certificate</p>
    </div>
  </div>
);

const RightHalf = () => (
  <div className="right-half">
    <img className="image" src={Microsoft} alt="Right Image" />
    <div>
      <h2>Right Half</h2>
      <p>This is the content for the right half of the section.</p>
    </div>

  </div>
);

export default SplitSection;
