// SplitSection.js
import React from 'react';
import { useInView } from 'react-intersection-observer';
import '../styles/SplitSection.css'; // Import your styles
import Microsoft from '../assets/microsoft.png';
import Google from '../assets/google.png';


const SplitSection = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.5,
  });

  return (
    <div className="split-section" ref={ref}>
      <LeftHalf inView={inView} />
      <RightHalf inView={inView} />
    </div>
  );
};

const LeftHalf = ({ inView }) => (
  <div className={`left-half ${inView ? 'show' : ''}`}>
    <img className="image" src={Microsoft} alt="Left Image" />
    <div>
      <h2>Left Half</h2>
      <p>This is the content for the left half of the section.</p>
    </div>
  </div>
);

const RightHalf = ({ inView }) => (
  <div className={`right-half ${inView ? 'show' : ''}`}>
    <div>
      <h2>Right Half</h2>
      <p>This is the content for the right half of the section.</p>
    </div>
    <img className="image" src={Google} alt="Right Image" />
  </div>
);

export default SplitSection;
